$(".input-number").keypress(function (key) {
	if (key.charCode == 0) return true;
	if (key.charCode < 48 || key.charCode > 57) return false;
});

//delete  choice by id
$(document).on("click", ".btn-clg-report", function (e) {
	var appl_reg_num = $(this).data("cand_code");
	var report = $(this).data("report");

	swal(
		{
			title: "Are you sure?",
			//	text: "You will not be able to edit the information once submitted.",
			type: "warning",
			showCancelButton: true,
			confirmButtonColor: "#F0404C",
			confirmButtonText: "Yes, I am sure!",
			cancelButtonText: "No, cancel it!",
			closeOnConfirm: true,
			closeOnCancel: true,
		},
		function (isConfirm) {
			//	alert(isConfirm);
			if (isConfirm) {
				update_clg_report(appl_reg_num, report);
			} else {
				e.preventDefault();
			}
		}
	);
});

function update_clg_report(appl_reg_num, report) {
	//var csrf_ax_token = $('input[name="csrf_ax_token"]').val();
	var param = "appl_reg_num=" + appl_reg_num + "&report=" + report;

	$.ajax({
		url: BASE_URL + "admin/update_clg_report",
		data: param,
		type: "get",
		dataType: "json",
		success: function (res) {
			if (res.data) {
				swal("Success", "Reported Successfully", "success");
			} else {
				swal("Error", "Reported Fail", "warning");
			}
			window.location = BASE_URL + "admin/listCourse";
			//$('input[name="csrf_ax_token"]').val(res.token);
		},
	});
}
